import React, { Component } from 'react';
import Login from './Login'
import Upgrade from './Upgrade'

export default class Shop extends Component {
	constructor(props){
		super(props)
		this.state = {page: "ppc"}
	}
  render() {
  	const {upgrades} = this.props
    return (
    	<div id="shopMenu">
    		<input type="button" value="PPC"/>
    		<input type="button" value="PPS"/>
    		<div id="ppc">
    			<p>PPC</p>
		      <Upgrade type="ppc" src={require("../img/chip.png")} prix="10" name="Intel Celeron" upgradeLvl={upgrades[0]} id="0" myCoin={this.props.myCoin} gameFunction={this.props.gameFunction}/>
		      <Upgrade type="ppc" src={require("../img/chip2.png")}prix="100" name="Intel I5" upgradeLvl={upgrades[1]} id="1" myCoin={this.props.myCoin} gameFunction={this.props.gameFunction}/>
	     	</div>
	     	<div id="pps">
	     		<p>PPS</p>
		      <Upgrade type="pps" src={require("../img/graphics-card.png")} prix="10" name="Intel Celeron" upgradeLvl={upgrades[2]} id="2" myCoin={this.props.myCoin} gameFunction={this.props.gameFunction}/>
		      <Upgrade type="pps" src={require("../img/graphics-card2.png")} prix="100" name="Intel I5" upgradeLvl={upgrades[3]} id="3" myCoin={this.props.myCoin} gameFunction={this.props.gameFunction}/>
	     	</div>
      </div>
    );
  }
}
