import React, { Component } from 'react';

export default class Upgrade extends Component {
	constructor(props){
		super(props)
		this.verifCoin = this.verifCoin.bind(this)
	}
	verifCoin(){
		const price = this.props.prix*this.props.upgradeLvl[this.props.id]*1.4
		const gain = this.props.prix*this.props.upgradeLvl[this.props.id]
		if(this.props.myCoin >= price){
			this.props.gameFunction(price,gain,this.props.id,this.props.type)
		}else{
			window.alert("NON")
		}
	}

  render() {
  	const {name,prix,upgradeLvl,src} = this.props
    return (
    	<div className="upgrade" onClick={this.verifCoin}>
	    	<img className="imgUpgrade" src={src}/>
	    	<div className="infoUpgrade">
		    	<p className="nameUpgrade" >{name}</p>
		    	<p className="infoUpgrade" >price : {prix*upgradeLvl*1.4}</p>
		    	<p className="infoUpgrade" >gain : {prix*upgradeLvl}</p>
	    	</div>
    	</div>
    );
  }
}
