import React, { Component } from 'react';

export default class lidlCoin extends Component {
	constructor(props){
		super(props)
		this.verifCoin = this.verifCoin.bind(this)
		var multiplicateur = 1.4;
		this.state = {name:"Intel I5",upgradeLvl:1,prix:100}
	}
	verifCoin(){
		if(this.props.myCoin >= this.state.prix*this.state.upgradeLvl*multiplicateur){
			this.setState({upgradeLvl:this.state.upgradeLvl+1,})
			this.props.gameFunction(this.state.prix*this.state.upgradeLvl,this.state.prix*this.state.upgradeLvl)
		}else{
			window.alert("NON")
		}
	}

  render() {
    return (
    	<div className="upgrade" onClick={this.verifCoin}>
	    	<img className="imgUpgrade" src="https://i.ytimg.com/vi/C4l-31lqlTc/hqdefault.jpg"/>
	    	<div className="infoUpgrade">
		    	<p className="nameUpgrade" >{this.state.name}</p>
		    	<p className="infoUpgrade" >price : {this.state.prix*this.state.upgradeLvl*multiplicateur}</p>
		    	<p className="infoUpgrade" >gain : {this.state.prix*this.state.upgradeLvl}</p>
	    	</div>
    	</div>
    );
  }
}
