import React, { Component } from 'react';

export default class pouceCoin extends Component {
	constructor(props){
		super(props)
		this.verifCoin = this.verifCoin.bind(this)
		this.state = {name:"pouceCoin",upgradeLvl:1,prix:10}
	}
	verifCoin(){
		if(this.props.myCoin >= this.state.prix*this.state.upgradeLvl){
			this.setState({upgradeLvl:this.state.upgradeLvl+1,})
			this.props.gameFunction(this.state.prix*this.state.upgradeLvl)
		}else{
			window.alert("NON")
		}
	}

  render() {
    return (
    	<button type="button" onClick={this.verifCoin}>{this.state.name}</button>
    );
  }
}
