import React, { Component } from 'react';
import Login from './Login'

export default class Game extends Component {
	constructor(props){
		super(props)
		this.updateCoin = this.updateCoin.bind(this)
		this.upgrade = this.upgrade.bind(this)
		this.state = {coin: 0, ppc: 1,upgradeLvl:1,prix:10}
	}
	updateCoin(){
		this.setState({coin: this.state.coin+this.state.ppc})
	}
	incrementPpc(){
		this.setState({ppc: this.state.ppc+this.state.upgradeLvl})
	}
	upgrade(){
		this.setState({coin: this.state.coin-(this.state.prix*this.state.upgradeLvl),upgradeLvl:this.state.upgradeLvl+1,ppc: this.state.ppc+this.state.upgradeLvl})
	}
  render() {
    return (
      <div className="App">
      <p>bonjour {this.props.playerName} !</p>
      <p> coins : {this.state.coin}</p>
      <button type="button" onClick={this.updateCoin}>Click me</button>
      <button type="button" onClick={this.upgrade}>Upgrade</button>
      </div>
    );
  }
}
