import React, { Component } from 'react';
import Login from './Login'
import Shop from './Shop'
import InfoBar from './InfoBar'

export default class Game extends Component {
	constructor(props){
		super(props)
		this.updateCoin = this.updateCoin.bind(this)
		this.upgrade = this.upgrade.bind(this)
		this.setShop = this.setShop.bind(this)
		this.state = {coin: 0, ppc: 1, shop: false,upgrades:[1,1],pps:0}
	}
	updateCoin(){
		this.setState({coin: this.state.coin+this.state.ppc})
	}
	incrementPpc(){
		this.setState({ppc: this.state.ppc+this.state.upgradeLvl})
	}
	upgrade(price,nbP,id,type){
		this.state.upgrades[id] = this.state.upgrades[id]+ 1
		switch(type){
			case "ppc": this.setState({coin: this.state.coin-price,ppc: this.state.ppc+nbP})
		}

	}
	setShop(){
		if(this.state.shop == false){
			this.setState({shop: true})
		}else{
			this.setState({shop: false})
		}
	}

  render() {
  	if(this.state.shop == true){
  		return (
	      <div className="App">
		    <InfoBar pps={this.state.pps} playerName={this.props.playerName} coin={this.state.coin} ppc={this.state.ppc}/>
		    <img id="clicker" src={require('../img/BG1.jpg')}  onClick={this.updateCoin} draggable="false"/>
		    <img id="shop" src={require('../img/shop.png')} onClick={this.setShop}/>
		    <Shop upgrades={this.state.upgrades} myCoin={this.state.coin} gameFunction={this.upgrade}/>
	      </div>
    );
  	}else{
  		return (
	      <div className="App">
	      	<InfoBar pps={this.state.pps} playerName={this.props.playerName} coin={this.state.coin} ppc={this.state.ppc}/>
		    <img id="clicker" src={require('../img/BG1.jpg')}  onClick={this.updateCoin} draggable="false" />
		    <img id="shop" src={require('../img/shop.png')} onClick={this.setShop}/>
	      </div>
    );
  	}

  }
}
