import React, { Component } from 'react';

export default class lidlCoin extends Component {
	constructor(props){
		super(props)
		this.verifCoin = this.verifCoin.bind(this)
		this.state = {name:"GPU",upgradeLvl:1,prix:100}
	}
	verifCoin(){
		if(this.props.myCoin >= this.state.prix*this.state.upgradeLvl){
			this.setState({upgradeLvl:this.state.upgradeLvl+1,})
			this.props.gameFunction(this.state.prix*this.state.upgradeLvl,this.state.upgradeLvl)
		}else{
			window.alert("NON")
		}
	}

  render() {
    return (
    	<div onClick={this.verifCoin}>{this.state.name}</div>
    );
  }
}
