import React, { Component } from 'react';
import Login from './Login'
import IntelCeleron from './upgrades/IntelCeleron'
import InetlI5 from './upgrades/IntelI5.js'

export default class Game extends Component {
	constructor(props){
		super(props)
		this.updateCoin = this.updateCoin.bind(this)
		this.upgrade = this.upgrade.bind(this)
		this.state = {coin: 0, ppc: 1}
	}
	updateCoin(){
		this.setState({coin: this.state.coin+this.state.ppc})
	}
	incrementPpc(){
		this.setState({ppc: this.state.ppc+this.state.upgradeLvl})
	}
	upgrade(price,nbP){
		console.log(nbP)
		this.setState({coin: this.state.coin-price,ppc: this.state.ppc+nbP})
	}
  render() {
    return (
    	<div>
	      <IntelCeleron myCoin={this.state.coin} gameFunction={this.upgrade}/>
	      <InetlI5 myCoin={this.state.coin} gameFunction={this.upgrade}/>
      </div>
    );
  }
}
