import React, { Component } from 'react';
import Login from './Login'

export default class Game extends Component {
	constructor(props){
		super(props)
		this.updateCoin = this.updateCoin.bind(this)
		this.state = {coin: 0, increment: 1}
	}
	updateCoin(){
		this.setState({coin: this.state.coin+this.state.increment})
		if(this.state.coin == 10){
			this.setState({increment: this.state.increment*2})
		}
	}
  render() {
    return (
      <div className="App">
      <p>bonjour {this.props.playerName} !</p>
      <p> coin : {this.state.coin}</p>
      <button type="button" onClick={this.updateCoin}>Click me</button>
      </div>
    );
  }
}
