import React, { Component } from 'react';

export default class pouceCoin extends Component {
	constructor(props){
		super(props)

		this.state = {name:"pouceCoin",upgradeLvl:1,prix:10}
	}
	verifCoin(){
		if(this.props.coin >= this.state.prix*this.state.upgradeLvl){
			this.setState({coin: this.state.coin-(this.state.prix*this.state.upgradeLvl),upgradeLvl:this.state.upgradeLvl+1,ppc: this.state.ppc+this.state.upgradeLvl})
		}else{
			window.alert("NON")
		}
	}

  render() {
    return (
    	<button type="button" onClick={this.verifCoin}>{this.state.name}</button>
    );
  }
}
