import React, { Component } from 'react';
import Login from './Login'
import Shop from './Shop'
import InfoBar from './InfoBar'
import Menu from './Menu'
import Miner from './Miner'

export default class Game extends Component {
	constructor(props){
		super(props)
		this.updateCoin = this.updateCoin.bind(this)
		this.upgrade = this.upgrade.bind(this)
		this.setShop = this.setShop.bind(this)
		this.state = {coin: 0, ppc: 1,page: "",upgradesPPC:[1,1],upgradesPPS:[1,1],pps:0}
	}
	updateCoin(){
		this.setState({coin: this.state.coin+this.state.ppc})
	}
	incrementPpc(){
		this.setState({ppc: this.state.ppc+this.state.upgradeLvl})
	}
	upgrade(price,nbP,id,type){
		this.state.upgrades[id] = this.state.upgrades[id]+ 1
		switch(type){
			case "ppc": this.setState({coin: this.state.coin-price,ppc: this.state.ppc+nbP})
			break;
			case "pps": this.setState({coin: this.state.coin-price,pps: this.state.pps+nbP})
			break;
		}

	}
	setPage(event){
		this.setState({page: event})
	}

  render() {
  	switch(this.state.page){
  		case "shop": return (
			      <div className="App">
				    <div id="windows" src={require('../img/BG1.jpg')} draggable="false">
				    	<div id="desktop">
					    	<Menu onClick={this.setShop} coinMine={this.updateCoin}/>
					    	<Shop upgradesPPS={this.state.upgradesPPS} upgradesPPC={this.state.upgradesPPC} myCoin={this.state.coin} gameFunction={this.upgrade}/>
				    	</div>
				    </div>
				    <InfoBar id="infoBar" pps={this.state.pps} playerName={this.props.playerName} coin={this.state.coin} ppc={this.state.ppc}/>
			      </div>
		    );
  		break;
  		case "miner": return (
			      <div className="App">
				    <div id="windows" src={require('../img/BG1.jpg')} draggable="false">
				    	<div id="desktop">
					    	<Menu onClick={this.setShop} coinMine={this.updateCoin}/>
					    	<Shop upgradesPPS={this.state.upgradesPPS} upgradesPPC={this.state.upgradesPPC} myCoin={this.state.coin} gameFunction={this.upgrade}/>
				    	</div>
				    </div>
				    <InfoBar id="infoBar" pps={this.state.pps} playerName={this.props.playerName} coin={this.state.coin} ppc={this.state.ppc}/>
			      </div>
		    );
  		break;
  		case "": return (
			      <div className="App">
				    <div id="windows" src={require('../img/BG1.jpg')} draggable="false">
				    	<div id="desktop">
					    	<Menu onClick={this.setShop} coinMine={this.updateCoin}/>
				    	</div>
				    </div>
				    <InfoBar id="infoBar" pps={this.state.pps} playerName={this.props.playerName} coin={this.state.coin} ppc={this.state.ppc}/>
