import React, { Component } from 'react';

export default class IntelCeleron extends Component {
	constructor(props){
		super(props)
		this.verifCoin = this.verifCoin.bind(this)
	}
	verifCoin(){
		if(this.props.myCoin >= this.state.prix*this.props.upgradeLvl*this.state.multiplicateur){
			this.setState({upgradeLvl:this.props.upgradeLvl+1,})
			this.props.gameFunction(this.state.prix*this.props.upgradeLvl*this.state.multiplicateur,this.state.prix*this.props.upgradeLvl,this.props.id)
		}else{
			window.alert("NON")
		}
	}

  render() {
    return (
    	<div className="upgrade" onClick={this.verifCoin}>
	    	<img className="imgUpgrade" src="https://i.ytimg.com/vi/C4l-31lqlTc/hqdefault.jpg"/>
	    	<div className="infoUpgrade">
		    	<p className="nameUpgrade" >{this.props.name}</p>
		    	<p className="infoUpgrade" >price : {this.state.prix*this.props.upgradeLvl*this.state.multiplicateur}</p>
		    	<p className="infoUpgrade" >gain : {this.state.prix*this.props.upgradeLvl}</p>
	    	</div>
    	</div>
    );
  }
}
